import React, { useEffect, useState } from "react";
import "./UserList.css"

const UserList = ({ socket }: any) => {
	const [userList, setUserList] = useState<{id: string, room: string, icon:any}[]>();
	const [iconImg, setIconImg] = useState<{data: string}| any>();

	useEffect(() => {
	  socket.on("UserList", (newUser:{id: string, room: string, icon:any}[]) => {

		setUserList(newUser);
	  });

	  return () => {
		socket.disconnect();
	  };
	}, [socket]);

	function decondeBase64(base64data: any){
		let base64ToString = Buffer.from(base64data, "base64").toString()
		setIconImg({data: base64ToString})
	}

	return (
		<div>
		{userList?.map(user=>
			<div className="userList">
				{decondeBase64(user.icon)}
				<img src={"data:image/jpeg;base64," + iconImg.data} />
				<div className="id">{user.id}</div>
				<div className="room">{user.room}</div>
			</div>
		)}
		</div>
	);
  };
  export default UserList;
