import React, { useEffect, useState } from "react";
import "./UserList.css"
import img_test from './gamer_boy.png';

const UserList = ({ socket }: any) => {
	const [userList, setUserList] = useState<{id: string, room: string, icon:any}[]>();

	useEffect(() => {
	  socket.on("UserList", (newUser:{id: string, room: string, icon:any}[]) => {

		setUserList(newUser);
	  });

	  return () => {
		socket.disconnect();
	  };
	}, [socket]);

	return (
		<div>
		{userList?.map(user=>
			<div className="userList">
				<img src={`data:image/jpeg;base64,${(user.icon).toString()}`} height="100" width="100" />
				<div className="id">{user.id}</div>
				<div className="room">{user.room}</div>
			</div>
		)}
			<div id="userList">
				<span className="userIcon">
					<img src={img_test} height="50" width="50" className="icon"/>
				</span>
				<span className="userInfo">
					<div className="id">id</div>
					<div className="room">room</div>
				</span>
			</div>
			<div className="id">id</div>
			<div className="room">room</div>
		</div>

	);
  };
  export default UserList;
