import React, { useEffect, useState } from "react";

const UserList = ({ socket }: any) => {

	const [userList, setUserList] = useState<{id: string, room: string}[]>();

	useEffect(() => {
	  console.log('test...................')
	  socket.on("UserList", (newUser:{id: string, room: string}[]) => {
		//setUserList((userList) => [...userList, {newUser}]);
		console.log('!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')
		console.log(newUser);
		setUserList(newUser);
	  });

	  return () => {
		socket.disconnect();
	  };
	}, [socket]);


	return (
		<div>
		{userList?.map(user=><div>{user.id}</div>)}
	  </div>
	);
  };
  export default UserList;
