import React, { useEffect, useState } from "react";

const UserList = ({ socket }: any) => {
	const [userList, setUserList] = useState<any[]>([]);

	useEffect(() => {
	  socket.on("UserList", (newUser: string) => {
		setUserList((userList) => [...userList, newUser]);
		console.log(newUser);
	  });

	  return () => {
		socket.disconnect();
	  };
	}, [socket]);

	return (
		<div>
		{userList}
	  </div>
	);
  };
  export default UserList;
