import React from "react";
import "./Home.css";
import 'bootstrap/dist/css/bootstrap.min.css';

interface HomeObj{
	userName:any,
	roomName:any,
	setUserName:any,
	setRoomName:any,
}

const Home = ({ userName, roomName, setUserName, setRoomName }: HomeObj) => {
  const handleUserNameChange = (e:any) => {
    setUserName(e.target.value);
  };
  const handleRoomNameChange = (e:any) => {
    setRoomName(e.target.value);
  };

  localStorage.setItem("userName", userName);
  localStorage.setItem("roomName", roomName);

  return (
    <div className="Home-container">
      <label htmlFor="roomName">Room</label>
      <input name="roomName" onChange={handleRoomNameChange}></input>
      <label htmlFor="id">ID</label>
      <input name="id" onChange={handleUserNameChange}></input>
	  <div className="Join-button">
	  	<a className="btn btn-outline-primary" href="/Chat"><b>채팅 참여하기</b></a>
	  </div>
	  <input type="radio" name="icon_img" value="gamer_boy.png">
		  <img src="../../../images/gamer_boy.png" width="50" height="50"></img>
	  </input>
	  <input type="radio" name="icon_img" value="gamer_girl.png">
		  <img src="../../../images/gamer_girl.png" width="50" height="50"></img>
	  </input>
    </div>
  );
};

export default Home;
