import React, { useEffect, useState } from "react";

const UserList = ({ socket }: any) => {
	const [userList, setUserList] = useState<{}[]>();

	useEffect(() => {
	  socket.on("UserList", (newUser:{}[]) => {
		setUserList(newUser);
	  });

	  return () => {
		socket.disconnect();
	  };
	}, [socket]);


	return (
		<div>
		{userList?.map(user=>
			<div>id: {user.id}, room: {user.room}</div>
		)}
	  </div>
	);
  };
  export default UserList;
