import React from "react";
import "./Home.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import gamer_boy from "../images/gamer_boy.png";
import gamer_girl from "../images/gamer_girl.png";

interface HomeObj{
	userName:any,
	roomName:any,
	setUserName:any,
	setRoomName:any,
}

const Home = ({ userName, roomName, setUserName, setRoomName }: HomeObj) => {
  const handleUserNameChange = (e:any) => {
    setUserName(e.target.value);
  };
  const handleRoomNameChange = (e:any) => {
    setRoomName(e.target.value);
  };

  localStorage.setItem("userName", userName);
  localStorage.setItem("roomName", roomName);

  function saveIconValue()
  {
	  var value:string = document.getElementById("iconValue")
	  localStorage.setItem("icon", document.getElementById("iconValue"));
  }


  return (
    <div className="Home-container">
      <label htmlFor="roomName">Room</label>
      <input name="roomName" onChange={handleRoomNameChange}></input>
      <label htmlFor="id">ID</label>
      <input name="id" onChange={handleUserNameChange}></input>

	  <span>
	  <input type="radio" name="radioTxt" value="gamer_boy" checked/>
	  <img src={gamer_boy} width="50" height="50"></img>
	  </span>
	  <span>
	  <input type="radio" name="radioTxt" value="gamer_girl" checked/>
	  <img src={gamer_girl} width="50" height="50"></img>
	  </span>

	  <div className="Join-button">
	  	<a className="btn btn-outline-primary" href="/Chat" id="iconValue" onClick={saveIconValue}><b>채팅 참여하기</b></a>
		<script>
			localStorage.setItem("icon", document.getElementById('iconValue'));
		</script>
	  </div>
    </div>
  );
};

export default Home;
