import React, {useState} from "react";
import "./styles/RoomList.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from './Modal';

interface HomeObj{
	roomName:any,
	setRoomName:(value:any)=>void,
}

const RoomList = ({ roomName, setRoomName}: HomeObj) => {
  const handleRoomNameChange = (e:any) => {
    setRoomName(e.target.value);
  };

  const [modalOpen, setModalOpen] = useState(false);
  const openModal = () => {
	setModalOpen(true);
  }
  const closeModal = () => {
	setModalOpen(false);
  }

  sessionStorage.setItem("roomName", roomName);

  return (
    <div className="RoomList-container">
		<div className="makeRoom-box">
			<a className="btn btn-primary btn-sm makeRoom" onClick={openModal}>방 만들기</a>
			<Modal open={modalOpen} close={closeModal} header="방 만들기">
				<label >방 제목</label>
				<input name="roomName" onChange={handleRoomNameChange}></input>
			</Modal>
		</div>

		<div className="RoomList-box">
			<div className="RoomList">
				방 목록...
			</div>
			<div className="Join-button">
				<a className="btn btn-dark" href="/Chat"><b>입장하기</b></a>
			</div>
		</div>
    </div>
  );
};

export default RoomList;
