import React from "react";
import "./Home.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import gamer_boy from "../images/gamer_boy.png";
import gamer_girl from "../images/gamer_girl.png";

interface HomeObj{
	userName:any,
	roomName:any,
	setUserName:any,
	setRoomName:any,
	icon:any,
	setIcon:any
}

const Home = ({ userName, roomName, setUserName, setRoomName, icon, setIcon }: HomeObj) => {
  const handleUserNameChange = (e:any) => {
    setUserName(e.target.value);
  };
  const handleRoomNameChange = (e:any) => {
    setRoomName(e.target.value);
  };
  const handleIconChange = (e:any) => {
    setIcon(e.target.value);
  };

  localStorage.setItem("userName", userName);
  localStorage.setItem("roomName", roomName);
  localStorage.setItem("icon", icon);

  return (
    <div className="Home-container">
      <label htmlFor="roomName">Room</label>
      <input name="roomName" onChange={handleRoomNameChange}></input>
      <label htmlFor="id">ID</label>
      <input name="id" onChange={handleUserNameChange}></input>

	  <span>
	  <input type="radio" name="icon" value="gamer_boy.png" checked onChange={handleIconChange} />
	  <img src={gamer_boy} width="50" height="50"></img>
	  </span>
	  <span>
	  <input type="radio" name="icon" value="gamer_girl.png" onChange={handleIconChange}/>
	  <img src={gamer_girl} width="50" height="50"></img>
	  </span>

	  <div className="Join-button">
	  	<a className="btn btn-outline-primary" href="/Chat"><b>채팅 참여하기</b></a>
	  </div>
    </div>
  );
};

export default Home;
